function main({input}) {
  try {
    // 提取输入中的文本内容
    const textContent = input.content[0].text;

    // 解析JSON字符串
    const parsedData = JSON.parse(textContent);

    // 检查是否存在结果数据
    if (parsedData && parsedData.result && parsedData.result.data) {
      // 提取state_description字段作为结果
      const stateDescription = parsedData.result.data.state_description;
      return {
        result: stateDescription,
      };
    } else {
      // 如果找不到相关数据，返回默认错误消息
      return {
        result: "无法获取生成状态",
      };
    }
  } catch (error) {
    // 处理解析错误
    console.error("处理输入数据时出错:", error);
    return {
      result: "数据处理错误",
    };
  }
}

function main({input}) {
  // 获取输入中的内容文本
  const contentText = input.content[0].text;

  try {
    // 解析JSON字符串获取数据
    const parsedData = JSON.parse(contentText);

    // 从解析后的数据中提取预览URL
    const previewUrl = parsedData.result.data.preview_url;

    // 返回预览URL作为结果
    return {
      result: previewUrl,
    };
  } catch (error) {
    // 如果解析失败，返回错误信息
    return {
      result: "Error processing input: " + error.message,
    };
  }
}

function main({input}) {
  // 解析input中的文本内容
