async function main(params) {
    // 获取输入数据
    const input = params.input || '';

    // 处理转义换行符
    const normalizedInput = input.replace(/\\n/g, '\n');

    // 提取所有符合表格格式的行
    const allLines = normalizedInput.split('\n');
    const tableLines = [];

    // 匹配表格行（支持复杂内容）
    const tableRowRegex = /^\s*\|(.+)\|\s*$/;
    for (const line of allLines) {
        const match = line.match(tableRowRegex);
        if (match) {
            tableLines.push(match[1].trim());
        }
    }

    if (tableLines.length < 2) {
        return { result: { fields: {} } };
    }

    // 处理表头
    const headers = tableLines[0].split('|').map(cell => cell.trim());

    // 判断分隔线位置
    let dataStartIndex = 1;
    if (isSeparatorLine(tableLines[1])) {
        dataStartIndex = 2;
    }

    // 处理数据行 - 只取第一行数据
    const fields = {};
    if (dataStartIndex < tableLines.length) {
        const cells = tableLines[dataStartIndex].split('|').map(cell => 
            cell.trim().replace(/<br\/?>/g, '\n') // 保留换行信息
        );

        for (let j = 0; j < headers.length; j++) {
            // 处理列数不足的情况
            fields[headers[j]] = cells[j] || '';
        }
    }

    // 构建输出对象
    const result = {
        fields: fields
    };

    return { result };
}

// 增强型分隔线判断
function isSeparatorLine(line) {
    return line.split('|').every(cell => 
        /^:?-+:?$/.test(cell.trim())
    );
}

