function main({input}) {
  // 将以竖线分隔的字符串转换为数组
  // 例如 "张三|三年级|20210001|13800138000" 会转换为 
  // ["张三", "三年级", "20210001", "13800138000"]
  const resultArray = input.split('|');

  return {
    result: resultArray,
  };
}

function main({input}) {
  // 定义飞书文档链接的正则表达式模式
  // 匹配格式: https://xxxxx.feishu.cn/base/xxxxxx?table=xxxxx&view=xxxxx
  const feishuUrlPattern = /^https:\/\/[a-zA-Z0-9]+\.feishu\.cn\/base\/[a-zA-Z0-9]+\?table=[a-zA-Z0-9]+&view=[a-zA-Z0-9]+$/;

  // 检查输入的URL是否匹配模式
  const isValid = feishuUrlPattern.test(input);

  return {
    result: isValid,
  };
}

function main(url) {
    // 如果是对象，提取URL字符串
    if (typeof url !== "string") {
        url = url?.data?.url || url?.url || "";
    }

    return {
        app_token: url.match(/\/base\/([^?]+)/)?.[1] || null,
        table_id: url.match(/table=([^&]+)/)?.[1] || null,
        view_id: url.match(/view=([^&]+)/)?.[1] || null
    };
}
